import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import "../styles/global.css";

export default function Landing() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
  }, [darkMode]);

  const features = [
    { title: "Real-Time Emotion Tracking", desc: "Track micro-expressions, voice tone, and gestures live.", link: "/interview-setup" },
    { title: "AI-Powered Reports", desc: "Get detailed candidate behavioral summaries instantly.", link: "/reports" },
    { title: "Customizable Feedback", desc: "Configure interview analytics to suit your needs.", link: "/dashboard" }
  ];

  return (
    <div className="page-container">
      <Navbar darkMode={darkMode} setDarkMode={setDarkMode} />

      <header className="hero">
        <motion.h1 initial={{ opacity: 0, y: -20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.8 }}>
          AI-Powered Interview Analytics
        </motion.h1>
        <p>Understand candidates beyond words â€” real-time emotional & behavioral insights.</p>
        <Link to="/interview-setup" className="cta-btn">Get Started</Link>
      </header>

      <section className="features">
        {features.map((f, i) => (
          <motion.div key={i} className="card" whileHover={{ scale: 1.05 }}>
            <h3>{f.title}</h3>
            <p>{f.desc}</p>
            <Link to={f.link} className="link-btn">Explore</Link>
          </motion.div>
        ))}
      </section>

      <Footer />
      </div>
  );
}
