import React from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import "../styles/theme.css"; // use your theme.css, not global.css

export default function HomePage() {
  const navigate = useNavigate();

  return (
    <div className="page-container" style={{ minHeight: "100vh", display: "flex", flexDirection: "column" }}>
      <Navbar />

      <main style={{
        flex: "1",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        background: "linear-gradient(135deg, #0f2027, #203a43, #2c5364)",
        color: "white",
        textAlign: "center",
        padding: "2rem"
      }}>
        <h1 style={{ fontSize: "3rem", marginBottom: "1rem" }}>HireVisor</h1>
        <p style={{ fontSize: "1.4rem", marginBottom: "2rem", opacity: 0.9 }}>
          Decode emotions, Enhance hiring
        </p>
        <button
          className="primary-btn"
          style={{
            padding: "1rem 2rem",
            fontSize: "1.2rem",
            borderRadius: "10px",
            background: "linear-gradient(90deg, #58c7e6, #2db7d8)",
            border: "none",
            cursor: "pointer",
            fontWeight: "bold",
            color: "#042334"
          }}
          onClick={() => navigate("/landing")}
        >
          Get Started
        </button>
      </main>

      <Footer />
    </div>
  );
}
