import React, { useState, useEffect } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import "../styles/global.css";
import { FaFilePdf, FaChartPie, FaSmile, FaFrown } from "react-icons/fa";

export default function Report() {
  const [darkMode, setDarkMode] = useState(
    () => localStorage.getItem("darkMode") === "true"
  );

  useEffect(() => {
    document.body.classList.toggle("dark-mode", darkMode);
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  return (
    <div className="page-container">
      <Navbar darkMode={darkMode} setDarkMode={setDarkMode} />

      <main className="report-page">
        <h1 className="gradient-text">Candidate Report</h1>
        <p className="dashboard-subtitle">
          AI-powered emotional and technical performance analysis
        </p>

        <section className="stats-grid">
          <div className="card glass-card">
            <FaSmile className="feature-icon" />
            <h3>Positive Responses</h3>
            <p>78%</p>
          </div>
          <div className="card glass-card">
            <FaFrown className="feature-icon" />
            <h3>Negative Responses</h3>
            <p>12%</p>
          </div>
          <div className="card glass-card">
            <FaChartPie className="feature-icon" />
            <h3>Technical Accuracy</h3>
            <p>85%</p>
          </div>
        </section>

        <div className="quick-actions">
          <button className="primary-btn"><FaFilePdf /> Download PDF</button>
        </div>
      </main>

      <Footer />
    </div>
  );
}
